import Child from "./Child";
import './Parent.css'

function Parent() {
  const [cnt, setCnt] = useState(0);
  const [boxes, setBoxes] = useState([
    { on: false, id: 1 },
    { on: false, id: 2 },
    { on: false, id: 3 },
    { on: false, id: 4 },
  ]);

  const handleTglStatus = (clkBox) => {
    setBoxes(
      boxes.map((box) => {
        box.on =
        clkBox.id === b.id ? !box.on : box.on;
        return clkBox;
      })
    );
    setCnt(boxes.reduce((curCnt, cur) => {
    if (cur.on) return curCnt + 1;
    else return curCnt;
    },0));
  };


  return (
    <div>
        <h1>Counter: {cnt}</h1>
      <div className="content">
      {boxes.map((box) => {
        return (
          <Child box={box} onTglStatus={handleTglStatus}>
          </Child>
        );
      })}
      </div>
    </div>
  );
}

export default Parent;
