import { combineReducers } from "redux";

const boxReducer = (
  boxes = [
    { on: false, id: 1 },
    { on: false, id: 2 },
    { on: false, id: 3 },
    { on: false, id: 4 },
  ],
  action
) => {
  return boxes.map((box) => {
    box.on = box.id === action.payload.id ? !box.on : box.on;
  });
};

const cntReducer = (cnt = 0, action) => {
  if (action.type === "ON") {
    return cnt - 1;
  } else return cnt + 1;
};

export default combineReducers({
  boxes: boxReducer,
  cnt: cntReducer,
});
