import { combineReducers } from 'redux';



const selectedBoxReducer = (selectedBox = null, action) => {
  if (action.type === 'BOX_SELECTED') {
    return action.payload;
  }

  return selectedBox;
};

export default combineReducers({
  selectedBox: selectedBoxReducer
});
